import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import ProviderCard from './components/ProviderCard';
import BookingModal from './components/BookingModal';
import AuthModal from './components/AuthModal';
import ProviderProfile from './components/ProviderProfile';
import { useRazorpay } from 'react-razorpay';

const App = () => {
  const [providers, setProviders] = useState([]);
  const [selectedProvider, setSelectedProvider] = useState(null);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [user, setUser] = useState(null);
  const [location, setLocation] = useState('');
  const { Razorpay } = useRazorpay();

  // Fetch user location using geolocation
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const res = await axios.get(
              `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`
            );
            setLocation(res.data.city || 'Delhi');
          } catch (err) {
            console.error('Geolocation error:', err);
            setLocation('Delhi');
          }
        },
        () => setLocation('Delhi')
      );
    }
  }, []);

  // Fetch providers
  useEffect(() => {
    const fetchProviders = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/providers?location=${location}`);
        setProviders(res.data);
      } catch (err) {
        console.error('Fetch providers error:', err);
      }
    };
    fetchProviders();
  }, [location]);

  // Check if user is logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/api/auth/me', {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => setUser(res.data))
        .catch(() => localStorage.removeItem('token'));
    }
  }, []);

  const handleSearch = async ({ query, location }) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/providers?query=${query}&location=${location}`
      );
      setProviders(res.data);
    } catch (err) {
      console.error('Search error:', err);
    }
  };

  const handleBooking = async (bookingData) => {
    if (!user) {
      setShowAuthModal(true);
      return;
    }
    try {
      const res = await axios.post('http://localhost:5000/api/bookings', bookingData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      const bookingId = res.data._id;
      const orderRes = await axios.post(
        'http://localhost:5000/api/payments/create-order',
        { amount: 500, bookingId }, // Example amount
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: orderRes.data.amount,
        currency: orderRes.data.currency,
        order_id: orderRes.data.orderId,
        handler: async (response) => {
          const verifyRes = await axios.post(
            'http://localhost:5000/api/payments/verify-payment',
            {
              orderId: response.razorpay_order_id,
              razorpayPaymentId: response.razorpay_payment_id,
              razorpaySignature: response.razorpay_signature,
              bookingId,
            },
            { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
          );
          alert(verifyRes.data.message);
        },
        prefill: { name: user.name, email: user.email },
        theme: { color: '#2563eb' },
      };

      const razorpayInstance = new Razorpay(options);
      razorpayInstance.open();
    } catch (err) {
      console.error('Booking error:', err);
      alert('Booking failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-800 text-white p-6 text-center">
        <h1 className="text-3xl font-bold">Local Service Finder</h1>
        <p className="mt-2">Find trusted service providers in your area</p>
        {user ? (
          <div className="flex justify-center gap-4 mt-4">
            <span>Welcome, {user.name}</span>
            <button
              onClick={() => {
                localStorage.removeItem('token');
                setUser(null);
              }}
              className="bg-red-600 text-white p-2 rounded-lg"
            >
              Logout
            </button>
          </div>
        ) : (
          <button
            onClick={() => setShowAuthModal(true)}
            className="bg-green-600 text-white p-2 rounded-lg mt-4"
          >
            Login / Register
          </button>
        )}
      </header>
      <main className="max-w-7xl mx-auto p-6">
        <SearchBar onSearch={handleSearch} initialLocation={location} />
        {selectedProvider ? (
          <ProviderProfile
            provider={selectedProvider}
            onBack={() => setSelectedProvider(null)}
            onBook={() => setShowBookingModal(true)}
          />
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
            {providers.map((provider) => (
              <ProviderCard
                key={provider._id}
                provider={provider}
                onSelect={() => setSelectedProvider(provider)}
                onBook={() => {
                  setSelectedProvider(provider);
                  setShowBookingModal(true);
                }}
              />
            ))}
          </div>
        )}
        <BookingModal
          provider={selectedProvider}
          isOpen={showBookingModal}
          onClose={() => setShowBookingModal(false)}
          onBook={handleBooking}
        />
        <AuthModal
          isOpen={showAuthModal}
          onClose={() => setShowAuthModal(false)}
          onAuth={(userData) => {
            setUser(userData);
            setShowAuthModal(false);
          }}
        />
      </main>
      <footer className="bg-gray-800 text-white p-4 text-center">
        <p>Â© 2025 Local Service Finder. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
