import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Search = () => {
  const [providers, setProviders] = useState([]);
  const [error, setError] = useState('');

  // Fetch all providers on component mount
  useEffect(() => {
    const fetchProviders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/provider', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        console.log('Fetched providers:', res.data);
        setProviders(res.data);
        setError('');
      } catch (err) {
        console.error('Fetch providers error:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch providers');
      }
    };
    fetchProviders();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Service Providers</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      {providers.length > 0 ? (
        <ul className="space-y-4">
          {providers.map((provider) => (
            <li key={provider._id} className="p-4 bg-gray-100 rounded-lg">
              <p><strong>{provider.name}</strong></p>
              <p>Service: {provider.service || 'Not specified'}</p>
              <p>Location: {provider.location || 'Unknown'}</p>
              {provider.photo && (
                <img
                  src={provider.photo}
                  alt={provider.name}
                  className="w-24 h-24 object-cover rounded-full mt-2"
                  onError={(e) => (e.target.src = 'https://via.placeholder.com/150')}
                />
              )}
              <Link
                to={`/provider/${provider._id}`}
                className="mt-2 inline-block bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
              >
                View Profile
              </Link>
            </li>
          ))}
        </ul>
      ) : (
        <p>No providers found</p>
      )}
    </div>
  );
};

export default Search;
