import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ServiceCard from './ServiceCard';

const ProviderDashboard = () => {
  const [services, setServices] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    duration: '',
    availability: [{ date: '', slots: [''] }],
  });
  const [editingService, setEditingService] = useState(null);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/services/my-services', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setServices(res.data);
      } catch (err) {
        console.error('Fetch services error:', err);
      }
    };
    fetchServices();
  }, []);

  const handleAddService = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/services', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setServices([...services, res.data]);
      setFormData({ name: '', description: '', price: '', duration: '', availability: [{ date: '', slots: [''] }] });
      alert('Service added successfully');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to add service');
    }
  };

  const handleUpdateService = async () => {
    try {
      const res = await axios.put(`http://localhost:5000/api/services/${editingService._id}`, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setServices(services.map((s) => (s._id === editingService._id ? res.data : s)));
      setEditingService(null);
      setFormData({ name: '', description: '', price: '', duration: '', availability: [{ date: '', slots: [''] }] });
      alert('Service updated successfully');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to update service');
    }
  };

  const handleEdit = (service) => {
    setEditingService(service);
    setFormData({
      name: service.name,
      description: service.description,
      price: service.price,
      duration: service.duration,
      availability: service.availability,
    });
  };

  const addAvailabilitySlot = () => {
    setFormData({
      ...formData,
      availability: [...formData.availability, { date: '', slots: [''] }],
    });
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Provider Dashboard</h2>
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold mb-4">{editingService ? 'Edit Service' : 'Add New Service'}</h3>
        <div className="grid gap-4">
          <input
            type="text"
            placeholder="Service Name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="p-2 border rounded-lg"
          />
          <textarea
            placeholder="Description"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="p-2 border rounded-lg"
            rows="4"
          />
          <input
            type="number"
            placeholder="Price (INR)"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            className="p-2 border rounded-lg"
          />
          <input
            type="text"
            placeholder="Duration (e.g., 1 hour)"
            value={formData.duration}
            onChange={(e) => setFormData({ ...formData, duration: e.target.value })}
            className="p-2 border rounded-lg"
          />
          {formData.availability.map((avail, index) => (
            <div key={index} className="flex gap-2">
              <input
                type="date"
                value={avail.date}
                onChange={(e) => {
                  const newAvailability = [...formData.availability];
                  newAvailability[index].date = e.target.value;
                  setFormData({ ...formData, availability: newAvailability });
                }}
                className="p-2 border rounded-lg"
              />
              <input
                type="text"
                placeholder="Slots (e.g., 10:00, 12:00)"
                value={avail.slots.join(',')}
                onChange={(e) => {
                  const newAvailability = [...formData.availability];
                  newAvailability[index].slots = e.target.value.split(',').map((s) => s.trim());
                  setFormData({ ...formData, availability: newAvailability });
                }}
                className="p-2 border rounded-lg flex-1"
              />
            </div>
          ))}
          <button
            onClick={addAvailabilitySlot}
            className="bg-gray-600 text-white p-2 rounded-lg hover:bg-gray-700"
          >
            Add Availability
          </button>
          <button
            onClick={editingService ? handleUpdateService : handleAddService}
            className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
            disabled={!formData.name || !formData.description || !formData.price || !formData.duration}
          >
            {editingService ? 'Update Service' : 'Add Service'}
          </button>
        </div>
      </div>
      <h3 className="text-lg font-semibold mt-6 mb-4">Your Services</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <ServiceCard key={service._id} service={service} onEdit={() => handleEdit(service)} />
        ))}
      </div>
    </div>
  );
};

export default ProviderDashboard;
