import React from 'react';

const ProviderCard = ({ provider, onSelect, onBook }) => {
  return (
    <div className="p-4 border rounded-lg shadow-md hover:shadow-lg transition">
      <img src={provider.photo} alt={provider.name} className="w-16 h-16 rounded-full mb-2" />
      <h3 className="text-lg font-semibold">{provider.name}</h3>
      <p className="text-gray-600">{provider.service}</p>
      <p className="text-gray-600">{provider.location}</p>
      <div className="flex items-center gap-2 mt-2">
        <span className="text-yellow-500">{'★'.repeat(Math.floor(provider.rating))}</span>
        <span className="text-gray-500">({provider.reviews.length} reviews)</span>
      </div>
      <div className="flex gap-4 mt-4">
        <button
          onClick={onSelect}
          className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
        >
          View Profile
        </button>
        <button
          onClick={onBook}
          className="bg-green-600 text-white p-2 rounded-lg hover:bg-green-700"
        >
          Book Now
        </button>
      </div>
    </div>
  );
};

export default ProviderCard;
