import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import ProviderCard from './ProviderCard';
import { Link, useNavigate } from 'react-router-dom';

const Search = () => {
  const [providers, setProviders] = useState([]);
  const [filteredProviders, setFilteredProviders] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Fetch all providers on page load
  useEffect(() => {
    const fetchProviders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/provider', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        console.log('Fetched providers:', res.data);
        setProviders(res.data);
        setFilteredProviders(res.data); // Display all providers initially
        setError('');
      } catch (err) {
        console.error('Fetch providers error:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch providers');
      }
    };
    fetchProviders();
  }, []);

  // Handle search from SearchBar
  const handleSearch = ({ query, location }) => {
    const filtered = providers.filter(
      (provider) =>
        (query
          ? provider.service.toLowerCase().includes(query.toLowerCase()) ||
            provider.name.toLowerCase().includes(query.toLowerCase())
          : true) &&
        (location
          ? provider.location.toLowerCase().includes(location.toLowerCase())
          : true)
    );
    setFilteredProviders(filtered);
  };

  // Handle provider selection (navigate to provider profile)
  const handleSelect = (providerId) => {
    navigate(`/provider/${providerId}`);
  };

  // Handle booking (placeholder, adjust as needed)
  const handleBook = (providerId) => {
    navigate(`/book/${providerId}`);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Find Service Providers</h2>
      <Link
        to="/provider-dashboard"
        className="mb-4 inline-block bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
      >
        Go to Provider Dashboard
      </Link>
      <SearchBar onSearch={handleSearch} initialLocation="" />
      {error && <p className="text-red-600 mb-4">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        {filteredProviders.length > 0 ? (
          filteredProviders.map((provider) => (
            <ProviderCard
              key={provider._id}
              provider={provider}
              onSelect={() => handleSelect(provider._id)}
              onBook={() => handleBook(provider._id)}
            />
          ))
        ) : (
          <p>No providers found</p>
        )}
      </div>
    </div>
  );
};

export default Search;
