import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import ProviderCard from './ProviderCard';

const Home = () => {
  const [providers, setProviders] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Fetch all providers on initial load
  useEffect(() => {
    const fetchProviders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/provider', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        console.log('Fetched providers:', res.data);
        setProviders(res.data);
        setError('');
      } catch (err) {
        console.error('Fetch providers error:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch providers');
      }
    };
    fetchProviders();
  }, []);

  

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Find Service Providers</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        {providers.length > 0 ? (
          providers.map((provider) => (
            <ProviderCard
              key={provider._id}
              provider={provider}

            />
          ))
        ) : (
          <p>No providers found</p>
        )}
      </div>
    </div>
  );
};

export default Home;
