import React, { useState } from 'react';

const BookingModal = ({ provider, isOpen, onClose, onBook }) => {
  const [date, setDate] = useState('');
  const [slot, setSlot] = useState('');
  const [notes, setNotes] = useState('');

  if (!isOpen || !provider) return null;

  const handleSubmit = () => {
    onBook({ providerId: provider._id, date, slot, notes });
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg max-w-md w-full">
        <h2 className="text-xl font-semibold mb-4">Book {provider.name}</h2>
        <p><strong>Service:</strong> {provider.service}</p>
        <p><strong>Location:</strong> {provider.location}</p>
        <div className="mt-4">
          <label className="block mb-2">Preferred Date</label>
          <select
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="p-2 border rounded-lg w-full"
          >
            <option value="">Select Date</option>
            {provider.availability.map((avail) => (
              <option key={avail.date} value={avail.date}>{avail.date}</option>
            ))}
          </select>
        </div>
        {date && (
          <div className="mt-4">
            <label className="block mb-2">Available Slots</label>
            <select
              value={slot}
              onChange={(e) => setSlot(e.target.value)}
              className="p-2 border rounded-lg w-full"
            >
              <option value="">Select Slot</option>
              {provider.availability.find((avail) => avail.date === date)?.slots.map((s) => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>
        )}
        <div className="mt-4">
          <label className="block mb-2">Additional Notes</label>
          <textarea
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            className="p-2 border rounded-lg w-full"
            rows="4"
          ></textarea>
        </div>
        <div className="flex gap-4 mt-6">
          <button
            onClick={onClose}
            className="bg-gray-600 text-white p-2 rounded-lg hover:bg-gray-700"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
            disabled={!date || !slot}
          >
            Confirm Booking
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookingModal;
