import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import SearchBar from './SearchBar';
import ProviderCard from './ProviderCard';
import ProviderProfile from './ProviderProfile';
import BookingModal from './BookingModal';
import { useRazorpay } from 'react-razorpay';

const Home = ({ user, setShowAuthModal }) => {
  const [providers, setProviders] = useState([]);
  const [selectedProvider, setSelectedProvider] = useState(null);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [error, setError] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();
  const { Razorpay } = useRazorpay();

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (id) {
          const res = await axios.get(`http://localhost:5000/api/provider/${id}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          });
          console.log('Fetched provider:', res.data);
          setSelectedProvider(res.data);
        } else {
          const res = await axios.get('http://localhost:5000/api/provider', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          });
          console.log('Fetched providers:', res.data);
          setProviders(res.data);
        }
        setError('');
      } catch (err) {
        console.error('Fetch error:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch providers');
      }
    };
    fetchData();
  }, [id]);

  const handleSearch = async ({ query, location }) => {
    try {
      const res = await axios.get('http://localhost:5000/api/provider', {
        params: { query, location },
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      console.log('Search results:', res.data);
      setProviders(res.data);
      setError('');
    } catch (err) {
      console.error('Search error:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Failed to search providers');
    }
  };

  const handleBooking = async (bookingData, onPaymentCancel) => {
    if (!user) {
      setShowAuthModal(true);
      return;
    }
    try {
      const orderRes = await axios.post(
        'http://localhost:5000/api/payments/create-order',
        { amount: bookingData.service.price, bookingId: bookingData.bookingId },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: orderRes.data.amount,
        currency: orderRes.data.currency,
        order_id: orderRes.data.orderId,
        handler: async (response) => {
          try {
            const verifyRes = await axios.post(
              'http://localhost:5000/api/payments/verify-payment',
              {
                orderId: response.razorpay_order_id,
                razorpayPaymentId: response.razorpay_payment_id,
                razorpaySignature: response.razorpay_signature,
                bookingId: bookingData.bookingId,
              },
              { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
            );
            alert(verifyRes.data.message);
            setShowBookingModal(false);
            const res = await axios.get('http://localhost:5000/api/provider');
            setProviders(res.data);
          } catch (err) {
            alert(err.response?.data?.message || 'Payment verification failed');
            onPaymentCancel();
          }
        },
        prefill: { name: user?.name, email: user?.email },
        theme: { color: '#2563eb' },
        modal: {
          ondismiss: () => {
            console.log('Razorpay modal closed without payment');
            onPaymentCancel();
          },
        },
      };

      const razorpayInstance = new Razorpay(options);
      razorpayInstance.open();
    } catch (err) {
      console.error('Booking error:', err);
      alert(err.response?.data?.message || 'Booking failed');
      onPaymentCancel();
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Find Service Providers</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      {selectedProvider ? (
        <ProviderProfile
          provider={selectedProvider}
          onBack={() => {
            setSelectedProvider(null);
            navigate('/');
          }}
          onBook={() => setShowBookingModal(true)}
        />
      ) : (
        <>
          <SearchBar onSearch={handleSearch} initialLocation="" />
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
            {providers.length > 0 ? (
              providers.map((provider) => (
                <ProviderCard
                  key={provider._id}
                  provider={provider}
                  onSelect={() => navigate(`/provider/${provider._id}`)}
                  onBook={() => {
                    setSelectedProvider(provider);
                    setShowBookingModal(true);
                  }}
                />
              ))
            ) : (
              <p>No providers found</p>
            )}
          </div>
        </>
      )}
      <BookingModal
        provider={selectedProvider}
        isOpen={showBookingModal}
        onClose={() => setShowBookingModal(false)}
        onBook={handleBooking}
      />
    </div>
  );
};

export default Home;
