import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import ProviderCard from './ProviderCard';

const Home = () => {
  const [providers, setProviders] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProviders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/provider', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setProviders(res.data);
        setError('');
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch providers');
      }
    };
    fetchProviders();
  }, []);

  const handleSearch = async ({ query, location }) => {
    try {
      const res = await axios.get('http://localhost:5000/api/provider', {
        params: { query, location },
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setProviders(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to search providers');
    }
  };

  const handleSelectProvider = (providerId) => {
    navigate(`/provider/${providerId}`);
  };

  const handleBookProvider = (providerId) => {
    navigate(`/book/${providerId}`);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Find Service Providers</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      <SearchBar onSearch={handleSearch} initialLocation="" />
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        {providers.length > 0 ? (
          providers.map((provider) => (
            <ProviderCard
              key={provider._id}
              provider={provider}
              onSelect={() => handleSelectProvider(provider._id)}
              onBook={() => handleBookProvider(provider._id)}
            />
          ))
        ) : (
          <p>No providers found</p>
        )}
      </div>
    </div>
  );
};

export default Home;
