import React, { useState } from 'react';
import ReviewSection from './ReviewSection';
import ServiceCard from './ServiceCard';

const ProviderProfile = ({ provider, onBack, onBook }) => {
  const [showReviews, setShowReviews] = useState(false);

  return (
    <div className="p-6 bg-white rounded-lg shadow-md">
      <button onClick={onBack} className="text-blue-600 mb-4 hover:underline">
        Back to Providers
      </button>
      <div className="flex gap-6">
        <img src={provider.photo} alt={provider.name} className="w-32 h-32 rounded-full" />
        <div>
          <h2 className="text-2xl font-semibold">{provider.name}</h2>
          <p className="text-gray-600">{provider.service}</p>
          <p className="text-gray-600">{provider.location}</p>
          <div className="flex items-center gap-2 mt-2">
            <span className="text-yellow-500">{'★'.repeat(Math.floor(provider.rating))}</span>
            <span className="text-gray-500">({provider.reviews.length} reviews)</span>
          </div>
        </div>
      </div>
      <div className="mt-6">
        <h3 className="text-lg font-semibold">Certifications</h3>
        <ul className="list-disc pl-6">
          {provider.certifications.map((cert, index) => (
            <li key={index}>{cert}</li>
          ))}
        </ul>
      </div>
      <div className="mt-6">
        <h3 className="text-lg font-semibold">Services</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
          {provider.services.map((service) => (
            <ServiceCard key={service._id} service={service} />
          ))}
        </div>
      </div>
      <div className="mt-6">
        <h3 className="text-lg font-semibold">Availability</h3>
        {provider.availability.map((avail, index) => (
          <div key={index} className="mt-2">
            <p><strong>{avail.date}</strong>: {avail.slots.join(', ')}</p>
          </div>
        ))}
      </div>
      <button
        onClick={onBook}
        className="mt-6 bg-green-600 text-white p-2 rounded-lg hover:bg-green-700"
      >
        Book Now
      </button>
      <button
        onClick={() => setShowReviews(!showReviews)}
        className="mt-4 text-blue-600 hover:underline"
      >
        {showReviews ? 'Hide Reviews' : 'Show Reviews'}
      </button>
      {showReviews && <ReviewSection providerId={provider._id} />}
    </div>
  );
};

export default ProviderProfile;
